CREATE VIEW `v_ufos` AS (select right(`c`.`Sichtungsdatum`,5) AS `year`,`a`.`name` AS `name`,`a`.`lat` AS `lat`,`a`.`lng` AS `lng`,`b`.`zipcode` AS `zipcode`,`c`.`Sichtungsdatum` AS `sighting`,`c`.`Sichtungsort` AS `place`,`c`.`Sichtungszeit` AS `time`,`c`.`Klassifikation` AS `classification`,`c`.`Bewertung` AS `rating`,`c`.`Identifikation` AS `identification` from ((`city` `a` join `zipcode` `b`) join `t_ufos` `c`) where ((`a`.`id` = `b`.`city_id`) and (`b`.`zipcode` = `c`.`PLZ`)));